import time
import pytest
import tempfile

from tg_sender import test_data
from tg_sender import tg_sender_api
from tg_sender import send_for_test

class TestTGFile:

    async def testTgSendFilePositive(self):
        with tempfile.NamedTemporaryFile() as tmp:
            tmp.write(b"wtf why did you open it")
            tmp.seek(0) #need to open later or will be empty read data
            tasks = [tg_sender_api.Task(
                channel = test_data.GetChannel(),
                send_file = tg_sender_api.SendFile(caption=None, path=tmp.name)
            )]
            await send_for_test.SendCompletelyForTest(test_data.GetTokens(), tasks)

    async def testTgSendFilePositiveCaption(self):
        with tempfile.NamedTemporaryFile() as tmp:
            tmp.write(b"wtf why did you open it")
            tmp.seek(0) #need to open later or will be empty read data
            tasks = [tg_sender_api.Task(
                channel = test_data.GetChannel(),
                send_file = tg_sender_api.SendFile(caption="caption test", path=tmp.name)
            )]
            await send_for_test.SendCompletelyForTest(test_data.GetTokens(), tasks)

    async def testTgSendFileNoFile(self):
        tasks = [tg_sender_api.Task(
                channel = test_data.GetChannel(),
                send_file = tg_sender_api.SendFile(caption=None, path="no_such_file")
            )]
        await send_for_test.SendCompletelyForTest(test_data.GetTokens(), tasks, 1)

