import time
import pytest
import tempfile
import os

from logger import logging
from tg_sender import test_data
from tg_sender import tg_sender_api
from tg_sender import send_for_test

from PIL import Image


class TestTGImage:

    async def testTgSendImagePositive(self):
        with tempfile.TemporaryDirectory() as tmpdir:
            img = Image.new('RGB', (32,32), color='white')
            file_name = os.path.join(tmpdir, "test.png")
            img.save(file_name)
            tasks = [tg_sender_api.Task(
                channel = test_data.GetChannel(),
                send_photo = tg_sender_api.SendPhoto(caption=None, path=file_name)
            )]
            await send_for_test.SendCompletelyForTest(test_data.GetTokens(), tasks)

