    async def testTgSendImagesPositive(self):
        with tempfile.TemporaryDirectory() as tmpdir:
            colors = ["red", "green", "blue"]
            file_names = []
            for i in range(len(colors)):
                img = Image.new('RGB', (32,32), color=colors[i])
                file_name = os.path.join(tmpdir, "test_{}.png".format(i))
                img.save(file_name)
                file_names.append(file_name)
            tasks = [tg_sender_api.Task(
                channel = test_data.GetChannel(),
                send_photos = tg_sender_api.SendPhotos(caption=None, paths=file_names)
            )]
            group_id = await send_for_test.SendCompletelyForTest(test_data.GetTokens(), tasks)
            
            tasks = [tg_sender_api.Task(
                channel=test_data.GetChannel(),
                forward= tg_sender_api.Forward(from_channel=test_data.GetChannel(), message_id=group_id)
            )]
            await send_for_test.SendCompletelyForTest(test_data.GetTokens(), tasks)

